contract_addresses = {
    'optimism':'0x4200000000000000000000000000000000000042',
    'arbitrum':'0xC4ed0A9Ea70d5bCC69f748547650d32cC219D882',
}


def get_staked_amount(chain, address):
    web3 = Web3(Web3.HTTPProvider(rpc_urls[chain]))
    abi = contract_abi[f"{chain.capitalize()}Contract"]
    contract = web3.eth.contract(address=contract_addresses[chain], abi=abi)
    staked_amount = contract.functions.balanceOf(Web3.to_checksum_address(address)).call()
    return staked_amount

for chain in ['optimism','arbitrum']:
    amount = get_staked_amount(chain, args.address)
    print(f"{chain.capitalize()}: {amount} tokens")